REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES
(32641, @GROUP_ID+0, 0, "Что, ты так меня и бросишь? А как же я домой вернусь?", 12, 0, 100, 6, 0, 0, 0, 'Дрикс Злобноверт to Player'),
(32642, @GROUP_ID+0, 0, 'Плохое у тебя колдунство! Не говори потом, что Моджодишу не предупреждала!', 12, 0, 100, 1, 0, 0, 0, 'Моджодишу to Player'),
(101002, @GROUP_ID+0, 0, 'Идите сюда! Смерть ждет вас!', 14, 0, 100, 0, 0, 58449, 0, 'Крос'),
(101002, @GROUP_ID+1, 0, 'Я сокрушу ваш мир!', 14, 0, 100, 0, 0, 58458, 0, 'Крос'),
(101002, @GROUP_ID+2, 0, 'Я сотру вас с лица земли...', 14, 0, 100, 0, 0, 58460, 0, 'Крос'),
(101002, @GROUP_ID+3, 0, 'Испытайте агонию!', 14, 0, 100, 0, 0, 58452, 0, 'Крос to Player'),
(101002, @GROUP_ID+4, 0, '|TInterface\\Icons\\Spell_Fire_FelFlameStrike:20|t Крос выбирает |cFFFF0404|3-3($n)|r целью заклинания |cFFFF0404|Hspell:205344|h["Сфера разрушения"]|h|r!', 41, 0, 100, 0, 0, 58452, 0, 'Крос to Player'),
(101002, @GROUP_ID+5, 0, '|TInterface\\Icons\\Spell_Holy_FistOfJustice:20|t Крос собирается уничтожить мост, применив |cFFFF0404|Hspell:205875|h[мощный удар]|h|r!', 41, 0, 100, 0, 0, 58460, 0, 'Крос'),
(101002, @GROUP_ID+6, 0, 'Весь ваш мир сгорит!', 14, 0, 100, 0, 0, 58457, 0, 'Крос to Player'),
(101002, @GROUP_ID+7, 0, 'Я разрушу ваши надежды!', 14, 0, 100, 0, 0, 58455, 0, 'Крос'),
(101002, @GROUP_ID+8, 0, 'Азерот... должен... сгореть...', 14, 0, 100, 0, 0, 58456, 0, 'Крос to Player'),
(102263, @GROUP_ID+0, 0, '|TInterface\\Icons\\Spell_Arcane_MassDispel:20|t %s начинает применять способность |cFFFF0000|Hspell:204471|h["Точечный удар"]|h|r.', 41, 0, 100, 0, 0, 0, 0, 'Скорпирон'),
(102263, @GROUP_ID+1, 0, '|TInterface\\Icons\\ability_skyreach_wind:20|t %s начинает произносить заклинание |cFFFF0000|Hspell:204316|h["Ударная волна"]|h|r.', 41, 0, 100, 0, 0, 0, 0, 'Скорпирон'),
(103685, @GROUP_ID+0, 0, 'Смертные ничтожества! Эти ночнорожденные не могут даже отстоять свой дворец! Была бы моя воля...', 14, 0, 100, 0, 0, 60127, 0, 'Тихондрий to Player'),
(103685, @GROUP_ID+1, 0, 'Но, думаю, мы уже скоро наградим наших "союзников" – а пока я тут за ними приберу!', 14, 0, 100, 0, 0, 60129, 0, 'Тихондрий to Player'),
(103685, @GROUP_ID+2, 0, 'Побили пару эльфов, пережравших магии, и решили, что одолеете истинного сына Легиона?!', 14, 0, 100, 0, 0, 60130, 0, 'Тихондрий to Player'),
(103685, @GROUP_ID+3, 0, 'Утоли мой голод!', 14, 0, 100, 0, 0, 60123, 0, 'Тихондрий to Player'),
(103685, @GROUP_ID+4, 0, 'Вы сгниете!', 14, 0, 100, 0, 0, 60121, 0, 'Тихондрий'),
(103685, @GROUP_ID+5, 0, 'Свет оставил вас!', 14, 0, 100, 0, 0, 60125, 0, 'Тихондрий'),
(103685, @GROUP_ID+6, 0, '|TInterface\\Icons\\spell_deathknight_necroticplague.blp:20|t Вы поражены |cFFF00000|Hspell:206480|h["Трупным заражением"]|h|r!', 42, 0, 100, 0, 0, 0, 0, 'Тихондрий to Player'),
(103685, @GROUP_ID+7, 0, 'Гибель близка!', 14, 0, 100, 0, 0, 60124, 0, 'Тихондрий'),
(103685, @GROUP_ID+8, 0, 'Но я... должен был победить...', 14, 0, 100, 0, 0, 60137, 0, 'Тихондрий to Player'),
(103758, @GROUP_ID+0, 0, 'Знали бы вы, что там, за гранью…', 14, 0, 100, 0, 0, 58430, 0, 'Звездный авгур Этрей'),
(103758, @GROUP_ID+1, 0, 'Звезды зажигаются – ваши жизни гаснут!', 14, 0, 100, 0, 0, 58435, 0, 'Звездный авгур Этрей'),
(103758, @GROUP_ID+2, 0, 'Страшитесь знамения звезд!', 14, 0, 100, 0, 0, 58433, 0, 'Звездный авгур Этрей'),
(103758, @GROUP_ID+3, 0, 'Звезды судили вас и сочли недостойными.', 14, 0, 100, 0, 0, 58445, 0, 'Звездный авгур Этрей'),
(103758, @GROUP_ID+4, 0, 'Не надейтесь... В будущем все еще хуже.', 14, 0, 100, 0, 0, 58448, 0, 'Звездный авгур Этрей to Player'),
(104154, @GROUP_ID+0, 0, 'О, а вот и наши герои. Такие самоуверенные. Именно это вас и погубит!', 14, 0, 100, 0, 0, 58777, 0, 'Гул''дан to Player'),
(104154, @GROUP_ID+1, 0, 'Вы уже забыли об унижении на Расколотом берегу? О том, что вашего вождя закололи, как свинью? Вы тоже будете медленно издыхать, умоляя меня прекратить ваши муки?', 14, 0, 100, 0, 0, 58779, 0, 'Гул''дан to Player'),
(104154, @GROUP_ID+2, 0, 'Ваша жалкая Орда рассыплется в прах. Этот мир достанется смерти – она уже заждалась.', 14, 0, 100, 0, 0, 58781, 0, 'Гул''дан to Player'),
(104154, @GROUP_ID+3, 0, 'Тело Иллидана станет идеальным сосудом для моего повелителя. Саргерас восстанет – и ваш мир сгорит!', 14, 0, 100, 0, 0, 58782, 0, 'Гул''дан to Player'),
(104154, @GROUP_ID+4, 0, 'Довольно игр, перейдем к делу!', 14, 0, 100, 0, 0, 58783, 0, 'Гул''дан to Player'),
(104154, @GROUP_ID+5, 0, 'Вам не остановить ритуал!', 14, 0, 100, 0, 0, 58772, 0, 'Гул''дан to Player'),
(104154, @GROUP_ID+6, 0, '|TInterface\\Icons\\Spell_Fire_FelPyroblast:20|t %s начинает произносить заклинание |cFFFF0000|Hspell:206219|h["Жидкое адское пламя"]|h|r.', 41, 0, 100, 0, 0, 0, 0, 'Гул''дан'),
(104154, @GROUP_ID+7, 0, 'Во славу Саргераса!', 14, 0, 100, 0, 0, 58790, 0, 'Гул''дан to Гул''дан'),
(104154, @GROUP_ID+8, 0, 'Сгорите изнутри!', 14, 0, 100, 0, 0, 58798, 0, 'Гул''дан to Гул''дан'),
(104154, @GROUP_ID+9, 0, 'Избавь меня от этих ничтожеств!', 14, 0, 100, 0, 0, 58791, 0, 'Гул''дан to Гул''дан'),
(104154, @GROUP_ID+10, 0, 'Убей их во имя повелителя!', 14, 0, 100, 0, 0, 58792, 0, 'Гул''дан to Гул''дан'),
(104154, @GROUP_ID+11, 0, 'Сосуд должен быть подготовлен!', 14, 0, 100, 0, 0, 58773, 0, 'Гул''дан to Гул''дан'),
(104154, @GROUP_ID+12, 0, 'Я испепелю вас!', 14, 0, 100, 0, 0, 58793, 0, 'Гул''дан'),
(104154, @GROUP_ID+13, 0, 'Вы проиграли, герои! Ритуал скоро завершится! Но сперва я все же прикончу вас!', 14, 0, 100, 0, 0, 58774, 0, 'Гул''дан to Player'),
(104154, @GROUP_ID+14, 0, 'Прочувствуйте мощь повелителя!', 14, 0, 100, 0, 0, 58799, 0, 'Гул''дан to Player'),
(104154, @GROUP_ID+15, 0, '|TInterface\\Icons\\ability_felarakkoa_feldetonation_red:20|t %s получает способность |cFFFF0000|Hspell:206221|h["Усиленные узы Скверны"]|h|r.', 41, 0, 100, 0, 0, 58795, 0, 'Гул''дан'),
(104154, @GROUP_ID+16, 0, 'Вам не избежать моей хватки!', 14, 0, 100, 0, 0, 58795, 0, 'Гул''дан'),
(104154, @GROUP_ID+17, 0, 'Смотрите, как сила обретает форму!', 14, 0, 100, 0, 0, 58796, 0, 'Гул''дан'),
(104154, @GROUP_ID+18, 0, '|TInterface\\Icons\\Spell_Fire_FelFlameStrike:20|t %s получает способность |cFFFF0000|Hspell:206220|h["Усиленное жидкое адское пламя"]|h|r.', 41, 0, 100, 0, 0, 58797, 0, 'Гул''дан'),
(104154, @GROUP_ID+19, 0, 'Упивайтесь славой!', 14, 0, 100, 0, 0, 58797, 0, 'Гул''дан'),
(104154, @GROUP_ID+20, 0, '|TInterface\\Icons\\Spell_Fire_FelFlameStrike:20|t %s начинает произносить заклинание |cFFFF0000|Hspell:206220|h["Усиленное жидкое адское пламя"]|h|r!', 41, 0, 100, 0, 0, 58793, 0, 'Гул''дан'),
(104154, @GROUP_ID+21, 0, 'Мою волю не сломить!', 14, 0, 100, 0, 0, 58794, 0, 'Гул''дан'),
(104154, @GROUP_ID+22, 0, '|TInterface\\Icons\\inv_misc_trinket6oih_orb1:20|t %s получает способность |cFFFF0000|Hspell:211152|h["Усиленное Око Гул''дана"]|h|r.', 41, 0, 100, 0, 0, 58776, 0, 'Гул''дан'),
(104154, @GROUP_ID+23, 0, 'Я не подведу тебя, повелитель! Мне нужно... больше силы...', 14, 0, 100, 0, 0, 58776, 0, 'Гул''дан'),
(104154, @GROUP_ID+24, 0, 'Хватит меня отвлекать! И так много работы...', 14, 0, 100, 0, 0, 58794, 0, 'Гул''дан to Гул''дан'),
(104154, @GROUP_ID+25, 0, 'Сдавайтесь – и, может быть, я пощажу вас!', 14, 0, 100, 0, 0, 58786, 0, 'Гул''дан to Гул''дан'),
(104154, @GROUP_ID+26, 0, 'Просто умрите! Зачем сопротивляться?', 14, 0, 100, 0, 0, 58775, 0, 'Гул''дан'),
(104288, @GROUP_ID+0, 0, 'Грязно, грязно, грязно!!!', 14, 0, 100, 0, 0, 58339, 0, 'Триллиакс to 78158'),
(104288, @GROUP_ID+1, 0, 'Вы везде наследили!', 14, 0, 100, 0, 0, 58335, 0, 'Триллиакс'),
(104288, @GROUP_ID+2, 0, '|TInterface\\Icons\\inv_pet_broom.blp:20|t %s призывает сферических уборщиков для |cFFF00000|Hspell:207513|h[уборки]|h|r.', 41, 0, 100, 0, 0, 0, 0, 'Триллиакс'),
(104415, @GROUP_ID+0, 0, 'Зачем противиться своей гибели?', 14, 0, 100, 0, 0, 58486, 0, 'Хрономатическая аномалия'),
(104415, @GROUP_ID+1, 0, 'Из Ночного Колодца выпадает угасающая частица времени.', 41, 0, 100, 0, 0, 0, 0, 'Хрономатическая аномалия to Хрономатическая аномалия'),
(104415, @GROUP_ID+2, 0, '|TInterface\\Icons\\inv_archonpet:20|t%s начинает читать заклинание |cFFFF0000|Hspell:211927|h["Переполняющая мощь"]|h|r!', 41, 0, 100, 0, 0, 0, 0, 'Хрономатическая аномалия to Хрономатическая аномалия'),
(104415, @GROUP_ID+3, 0, 'Мое время пришло.', 14, 0, 100, 0, 0, 58496, 0, 'Хрономатическая аномалия to Player'),
(104528, @GROUP_ID+0, 0, 'Красота Сурамара — моя заслуга! Я овладел искусством вплетения магии в саму природу и все ее творения!', 14, 0, 100, 0, 0, 68626, 0, 'Верховный ботаник Тел''арн to Player'),
(104528, @GROUP_ID+1, 0, 'Как вы посмели вторгнуться в мои сады?', 14, 0, 100, 0, 0, 68634, 0, 'Верховный ботаник Тел''арн to Player'),
(104528, @GROUP_ID+2, 0, 'Эй, не опалите бутоны, пока горите сами!', 14, 0, 100, 0, 0, 68642, 0, 'Верховный ботаник Тел''арн'),
(104528, @GROUP_ID+3, 0, 'Один вредитель уничтожит другого!', 14, 0, 100, 0, 0, 68641, 0, 'Верховный ботаник Тел''арн'),
(104528, @GROUP_ID+4, 0, 'Увядайте от жары, сорняки!', 14, 0, 100, 0, 0, 68629, 0, 'Верховный ботаник Тел''арн'),
(104528, @GROUP_ID+5, 0, 'Вы надеетесь выстоять против трех первобытных сил, творящих саму жизнь?!', 14, 0, 100, 0, 0, 68642, 0, 'Верховный ботаник Тел''арн'),
(104528, @GROUP_ID+6, 0, '|TINTERFACE\\ICONS\\Spell_Arcane_PrismaticCloak:20|t Чародей Тел''арн начинает произносить заклинание |cFFF00000|Hspell:218807|h["Зов ночи"]|h|r!', 41, 0, 100, 0, 0, 68635, 0, 'Чародей Тел''арн'),
(104528, @GROUP_ID+7, 0, 'Ночь наступает!', 14, 0, 100, 0, 0, 68635, 0, 'Чародей Тел''арн'),
(104528, @GROUP_ID+8, 0, 'Кто-нибудь... полейте цветы.', 14, 0, 100, 0, 0, 68631, 0, 'Чародей Тел''арн to Player'),
(104537, @GROUP_ID+0, 0, '|TInterface\\Icons\\inv_axe_2h_felfireraid_d_01:20|t %s подготавливает способность |cFFFF0000|Hspell:206675|h["Раскалывание сущности"]|h|r.', 41, 0, 100, 0, 0, 58792, 0, 'Повелитель Скверны Кураз''мал to Player'),
(104881, @GROUP_ID+0, 0, 'Я дала вам шанс ударить первыми – а вы такие немощные?', 14, 0, 100, 0, 0, 58394, 0, 'Заклинательница клинков Алуриэль'),
(104881, @GROUP_ID+1, 0, 'Сейчас вы увидите мастера в действии!', 14, 0, 100, 0, 0, 58410, 0, 'Заклинательница клинков Алуриэль'),
(104881, @GROUP_ID+2, 0, 'Смотрите под ноги – не ровен час, поскользнетесь!', 14, 0, 100, 0, 0, 58415, 0, 'Заклинательница клинков Алуриэль'),
(104881, @GROUP_ID+3, 0, 'Репликация!', 14, 0, 100, 0, 0, 58398, 0, 'Заклинательница клинков Алуриэль'),
(104881, @GROUP_ID+4, 0, 'Учитесь, пока я жива!', 14, 0, 100, 0, 0, 58418, 0, 'Заклинательница клинков Алуриэль'),
(104881, @GROUP_ID+5, 0, 'Детонация!', 14, 0, 100, 0, 0, 58399, 0, 'Заклинательница клинков Алуриэль'),
(104881, @GROUP_ID+6, 0, 'Анимация!', 14, 0, 100, 0, 0, 58400, 0, 'Заклинательница клинков Алуриэль'),
(104881, @GROUP_ID+7, 0, 'Вам не сравниться со мной в мастерстве!', 14, 0, 100, 0, 0, 58419, 0, 'Заклинательница клинков Алуриэль'),
(104881, @GROUP_ID+8, 0, 'Аннигиляция!', 14, 0, 100, 0, 0, 58401, 0, 'Заклинательница клинков Алуриэль'),
(104881, @GROUP_ID+9, 0, 'Госпожа... Я подвела тебя...', 14, 0, 100, 0, 0, 58406, 0, 'Заклинательница клинков Алуриэль to Player'),
(106522, @GROUP_ID+0, 0, 'Вернем душу Иллидана в тело... Владыка Легиона не должен его заполучить.', 14, 0, 100, 0, 0, 68560, 0, 'Верховный маг Кадгар to Гул''дан'),
(106522, @GROUP_ID+1, 0, 'Мы его остановим!', 14, 0, 100, 0, 0, 58946, 0, 'Верховный маг Кадгар to Гул''дан'),
(106522, @GROUP_ID+2, 0, 'Не сдавайтесь! Гул''дан вот-вот дрогнет! Мы победим!', 14, 0, 100, 0, 0, 58943, 0, 'Верховный маг Кадгар to Гул''дан'),
(106643, @GROUP_ID+0, 0, 'Ритуал прерван. Фигура в Ночном Колодце пробуждается.', 16, 0, 100, 0, 0, 0, 0, 'Элисанда to Элисанда'),
(106643, @GROUP_ID+1, 0, 'Ваше время вышло!', 14, 0, 100, 0, 0, 58561, 0, 'Элисанда'),
(106643, @GROUP_ID+2, 0, 'Я здесь доминирую, не вы!', 14, 0, 100, 0, 0, 58565, 0, 'Элисанда'),
(106643, @GROUP_ID+3, 0, 'Волны времени сметут вас!', 14, 0, 100, 0, 0, 58566, 0, 'Элисанда'),
(106643, @GROUP_ID+4, 0, '|TInterface\\Icons\\Spell_Mage_ArcaneOrb.blp:20|t |cFFFF0000|Hspell:228877|h[Кинетические кольца]|h|r схлопываются, двигаясь от края комнаты к центру!', 41, 0, 100, 0, 0, 0, 0, 'Элисанда to Элисанда'),
(106643, @GROUP_ID+5, 0, 'Вы живы лишь здесь и сейчас. А мои возможности безграничны!', 14, 0, 100, 11, 0, 58568, 0, 'Элисанда'),
(106643, @GROUP_ID+6, 0, 'Время нестабильно – сейчас вы сами в этом убедитесь.', 14, 0, 100, 0, 0, 58567, 0, 'Элисанда'),
(106643, @GROUP_ID+7, 0, '|TInterface\\Icons\\SPELL_ARCANE_INVOCATION:20|t С неба падают |cFFFF0000|Hspell:210022|h[эпохальные сферы]|h|r!', 41, 0, 100, 0, 0, 58567, 0, 'Элисанда to Элисанда'),
(106643, @GROUP_ID+8, 0, 'Мне подвластно само время!', 14, 0, 100, 0, 0, 58562, 0, 'Элисанда'),
(106643, @GROUP_ID+9, 0, '|TInterface\\Icons\\Spell_Mage_ArcaneOrb.blp:20|t%s направляет на своих врагов |cFFFF0000|Hspell:214278|h[пророческий луч]|h|r !', 41, 0, 100, 0, 0, 0, 0, 'Элисанда to Элисанда'),
(106643, @GROUP_ID+10, 0, 'Времена меняются! Ваша смерть все ближе!', 14, 0, 100, 11, 0, 58569, 0, 'Элисанда'),
(106643, @GROUP_ID+11, 0, 'Я управляю потоками времени! Вам не победить!', 14, 0, 100, 0, 0, 58570, 0, 'Элисанда'),
(106643, @GROUP_ID+12, 0, 'Время... уходит! Как это возможно?', 14, 0, 100, 0, 0, 58571, 0, 'Элисанда'),
(106643, @GROUP_ID+13, 0, 'Хотите пронзить ткань времени? Или навсегда остаться в Вечности?', 14, 0, 100, 0, 0, 58563, 0, 'Элисанда'),
(106643, @GROUP_ID+14, 0, '|TInterface\\Icons\\6BF_Explosive_Shard.blp:20|t%s пускает импульсы |cFFFF0000|Hspell:209598|h[искривляющей]|h|r энергии в своих противников!', 41, 0, 100, 0, 0, 58563, 0, 'Элисанда to Элисанда'),
(106643, @GROUP_ID+15, 0, 'Немыслимо! Победа должна была достаться мне...', 14, 0, 100, 0, 0, 58575, 0, 'Элисанда to Player'),
(109038, @GROUP_ID+0, 0, 'Опустится тьма... умрете и вы!', 14, 0, 100, 0, 0, 68638, 0, 'Солнцевед Тел''арн'),
(109041, @GROUP_ID+0, 0, 'Природа кормит тех, кто ее ценит.', 14, 0, 100, 0, 0, 68637, 0, 'Натуралист Тел''арн'),
(110791, @GROUP_ID+0, 0, 'Друзья, это знаменательный день. До пришествия Легиона вход в Цитадель Ночи был закрыт для чужеземцев десять тысяч лет.', 14, 0, 100, 0, 0, 67739, 0, 'Первая чародейка Талисра to Player'),
(110791, @GROUP_ID+1, 0, 'Теперь только вы... только вы, чужеземцы можете спасти мой народ… от ужасной судьбы, на которую его обрекла Элисанда.', 14, 0, 100, 0, 0, 67737, 0, 'Первая чародейка Талисра to Молниеносец'),
(110791, @GROUP_ID+2, 0, 'Шанар далорас! Не представляю, как могло возникнуть нечто подобное, но в нем живет энергия Ночного Колодца.', 14, 0, 100, 0, 0, 67738, 0, 'Первая чародейка Талисра to 111081'),
(110791, @GROUP_ID+3, 0, 'От этой аберрации исходят лучи силы. Должно быть, именно она помогает Элисанде поддерживать ловушку времени.', 14, 0, 100, 0, 0, 78870, 0, 'Первая чародейка Талисра to 111081'),
(110791, @GROUP_ID+4, 0, 'Мы должны уничтожить тварь, чтобы освободить наших друзей у ворот!', 14, 0, 100, 0, 0, 78871, 0, 'Первая чародейка Талисра to 111081'),
(110791, @GROUP_ID+5, 0, 'Дворец прямо перед вами. Но охраняется он еще сильнее, чем нижние ярусы.', 14, 0, 100, 0, 0, 67740, 0, 'Первая чародейка Талисра to 78158'),
(111227, @GROUP_ID+0, 0, 'Слишком поздно! Крос принесет вам погибель!', 14, 0, 100, 0, 0, 0, 0, 'Заклинательница Скверны Фарамера'),
(112660, @GROUP_ID+0, 0, 'Посторонние? Здесь?!', 14, 0, 100, 1, 0, 0, 0, 'Житель Цитадели Ночи to Player'),
(112660, @GROUP_ID+1, 0, 'Стража! Помогите!', 14, 0, 100, 1, 0, 0, 0, 'Житель Цитадели Ночи to Player'),
(112660, @GROUP_ID+2, 0, 'Пожалуйста, только не бей.', 12, 0, 100, 1, 0, 0, 0, 'Жительница Цитадели Ночи to Player'),
(117085, @GROUP_ID+0, 0, 'Наконец-то свобода! Спасибо!', 12, 0, 100, 603, 0, 78646, 0, 'Ли''лет Лунарх to Player'),
(117085, @GROUP_ID+1, 0, 'Я говорила Алуриэль, что за мной придут – а она смеялась в ответ. Поделом ей!', 12, 0, 100, 1, 0, 78647, 0, 'Ли''лет Лунарх to Player');
